<section class="tech-stack py-24 bg-gray-900/50 px-4 md:px-8">
	<div class="container max-w-6xl mx-auto">
		<h2 class="section-title mb-12 text-center reveal">Tech Stack</h2>

		<div class="stack-grid grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4 md:gap-6">
			{#each techStack as tech}
				<div
					class="stack-item group flex flex-col items-center p-6 bg-gray-800 rounded-xl transition-all duration-300 hover:-translate-y-2 hover:bg-electric-blue/20 border border-transparent hover:border-electric-blue/50"
				>
					<div
						class="icon-container w-16 h-16 md:w-20 md:h-20 mb-4 flex items-center justify-center"
					>
						{@html iconMap[tech.icon] || '<div class="text-4xl">âš¡</div>'}
					</div>
					<p class="stack-name text-base md:text-lg font-semibold mb-1">{tech.name}</p>
					<p
						class="stack-context text-xs md:text-sm text-gray-400 text-center opacity-0 group-hover:opacity-100 transition-opacity duration-300"
					>
						{tech.context}
					</p>
				</div>
			{/each}
		</div>
	</div>
</section>